import React, { useEffect, useState } from 'react';
import './PacketVisualization.css'; // Import CSS for styling and animations

const PacketVisualization = ({ packetData }) => {
  const [currentPacket, setCurrentPacket] = useState(null);

  useEffect(() => {
    if (packetData.length > 0) {
      const newPacket = packetData[packetData.length - 1];
      setCurrentPacket(newPacket);
    }
  }, [packetData]);

  const renderLayer = (layerData, layerName) => (
    <div className="osi-layer">
      <h4>{layerName}</h4>
      <pre>{JSON.stringify(layerData, null, 2)}</pre>
    </div>
  );

  return (
    <div className="packet-visualization">
      <h3>Real-Time Packet Data</h3>
      {currentPacket ? (
        <div className="packet-container">
          <h4>Packet #{packetData.length}</h4>
          <div>
            <strong>Packet Timestamp:</strong> {currentPacket.timestamp || 'N/A'}
          </div>
          <div className="osi-layers">
            {renderLayer(currentPacket.layers?.http, 'Application Layer (HTTP)')}
            {renderLayer(currentPacket.layers?.ip, 'Network Layer (IP)')}
            {renderLayer(currentPacket.layers?.tcp, 'Transport Layer (TCP)')}
            {renderLayer(currentPacket.layers?.frame, 'Data Link Layer (Frame)')}
            <div>
              <strong>Physical Layer (Packet Size):</strong> {`${currentPacket.size || 'N/A'} bytes`}
            </div>
          </div>
        </div>
      ) : (
        <p>No packets captured yet...</p>
      )}
    </div>
  );
};

export default PacketVisualization;
