// src/App.js
import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';


const socket = io();

function App() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [username, setUsername] = useState('');
    const [packets, setPackets] = useState([]);

    useEffect(() => {
        socket.on('chatMessage', (msg) => {
            setMessages((prevMessages) => [...prevMessages, msg]);
        });

        socket.on('packetData', (packet) => {
            setPackets((prevPackets) => [...prevPackets, packet]);
        });

        return () => {
            socket.off('chatMessage');
            socket.off('packetData');
        };
    }, []);

    const sendMessage = (e) => {
        e.preventDefault();
        if (input && username) {
            socket.emit('chatMessage', { username, message: input });
            setInput('');
        }
    };

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    return (
        <div className="App">
            <h1>Packet Capture and Chat</h1>
            <h1>Packet Capture and Chat</h1>
            {!username ? (
                <div>
                    <input
                        type="text"
                        placeholder="Enter your username"
                        onChange={handleUsernameChange}
                    />
                </div>
            ) : (
                <div>
                    <h2>Welcome, {username}!</h2>
                    <div>
                        <h2>Chat</h2>
                        <ul>
                            {messages.map((msg, index) => (
                                <li key={index}>
                                    <strong>{msg.username}: </strong>{msg.message}
                                </li>
                            ))}
                        </ul>
                        <form onSubmit={sendMessage}>
                            <input
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                placeholder="Type a message"
                            />
                            <button type="submit">Send</button>
                        </form>
                    </div>
                    <div>
                        <h2>Captured Packets</h2>
                        {packets.length > 0 ? (
                            <pre>{JSON.stringify(packets, null, 2)}</pre>
                        ) : (
                            <p>No packets have been captured yet.</p>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
