import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import PacketVisualization from './PacketVisualization';

// Connect to backend server
const socket = io('http://localhost:3001');

const App = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [packetFlow, setPacketFlow] = useState({});

    const handleSendMessage = () => {
        if (!message.trim()) return;

        const msg = { sender: socket.id, receiver: 'all', message };
        socket.emit('sendMessage', msg);
        setMessage('');
    };

    useEffect(() => {
        socket.on('receiveMessage', (data) => {
            setMessages((prev) => [...prev, data]);

            const newPacketFlow = {
                Application: `Message received: "${data.message}"`,
                Transport: `Source port: 3001, Dest port: 8080`,
                Network: `IP Packet from ${data.sender} to all`,
                DataLink: `Frame: Data encapsulated for transmission`,
                Physical: `Bits transmitted over network`,
            };

            setPacketFlow(newPacketFlow);
        });

        return () => {
            socket.off('receiveMessage');
        };
    }, []);

    return (
        <div>
            <h1>Anonymous Chat</h1>
            <div style={{ border: '1px solid #ddd', padding: '10px', maxHeight: '200px', overflowY: 'scroll' }}>
                {messages.map((msg, idx) => (
                    <p key={idx}>
                        <strong>{msg.sender}:</strong> {msg.message}
                    </p>
                ))}
            </div>
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type your message..."
            />
            <button onClick={handleSendMessage}>Send</button>

            <PacketVisualization packetFlow={packetFlow} />
        </div>
    );
};

export default App;
