import React, { useEffect } from 'react';
import * as d3 from 'd3';

const PacketVisualization = ({ packetData }) => {
    useEffect(() => {
        const svg = d3.select('#packet-visualization');
        svg.selectAll('*').remove(); // Clear previous visuals

        const width = 800;
        const height = 200;
        const margin = { top: 20, bottom: 20, left: 20, right: 20 };

        const xScale = d3.scaleLinear()
            .domain([0, packetData.length - 1])
            .range([margin.left, width - margin.right]);

        svg.attr('viewBox', [0, 0, width, height]);

        // Draw circles for packets
        svg.selectAll('circle')
            .data(packetData)
            .join('circle')
            .attr('cx', (_, i) => xScale(i))
            .attr('cy', height / 2)
            .attr('r', 20)
            .attr('fill', 'blue')
            .on('mouseover', (_, d) => {
                d3.select('#tooltip')
                    .style('opacity', 1)
                    .text(`Src: ${d.src}, Dst: ${d.dst}, Length: ${d.len}`);
            })
            .on('mouseout', () => {
                d3.select('#tooltip').style('opacity', 0);
            });

        // Add tooltip
        const tooltip = d3.select('#tooltip');
        if (!tooltip.size()) {
            d3.select('body').append('div')
                .attr('id', 'tooltip')
                .style('position', 'absolute')
                .style('background', 'rgba(0, 0, 0, 0.8)')
                .style('color', 'white')
                .style('padding', '5px')
                .style('border-radius', '5px')
                .style('opacity', 0);
        }
    }, [packetData]);

    return (
        <div>
            <h2>Real-Time Packet Visualization</h2>
            <svg id="packet-visualization" width="800" height="200"></svg>
        </div>
    );
};

export default PacketVisualization;
