import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001');

const App = () => {
    const [rooms, setRooms] = useState([]);
    const [currentRoom, setCurrentRoom] = useState('');
    const [username, setUsername] = useState('');
    const [isUsernameSet, setIsUsernameSet] = useState(false);
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [newRoom, setNewRoom] = useState('');

    const handleSetUsername = () => {
        if (username.trim()) setIsUsernameSet(true);
    };

    const handleCreateRoom = async () => {
        if (newRoom.trim()) {
            try {
                const response = await fetch('http://localhost:3001/rooms', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ name: newRoom }),
                });
                const room = await response.json();
                setRooms((prevRooms) => [...prevRooms, room]);
                setNewRoom('');
            } catch (error) {
                console.error('Error creating room:', error);
            }
        }
    };

    const handleJoinRoom = (roomName) => {
        setCurrentRoom(roomName);
        socket.emit('joinRoom', roomName);

        // Fetch messages for the room
        fetch(`http://localhost:3001/messages/${roomName}`)
            .then((res) => res.json())
            .then((data) => setMessages(data))
            .catch((err) => console.error('Error fetching room messages:', err));
    };

    const handleSendMessage = () => {
        if (message.trim() && currentRoom) {
            const chatMessage = { username, message, room: currentRoom };
            socket.emit('chatMessage', chatMessage);
            setMessage('');
        }
    };

    useEffect(() => {
        // Fetch available rooms
        fetch('http://localhost:3001/rooms')
            .then((res) => res.json())
            .then((data) => setRooms(data))
            .catch((err) => console.error('Error fetching rooms:', err));

        socket.on('chatMessage', (data) => {
            setMessages((prevMessages) => [...prevMessages, data]);
        });

        return () => {
            socket.off('chatMessage');
        };
    }, []);

    return (
        <div className="app-container">
            {!isUsernameSet ? (
                <div className="username-container">
                    <h2>Enter your username</h2>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter username"
                        className="input-field"
                    />
                    <button onClick={handleSetUsername} className="submit-button">
                        Submit
                    </button>
                </div>
            ) : (
                <div className="chat-container">
                    <h2>Welcome, {username}!</h2>
                    <div className="room-management">
                        <h3>Rooms</h3>
                        <ul>
                            {rooms.map((room) => (
                                <li key={room._id} onClick={() => handleJoinRoom(room.name)}>
                                    {room.name}
                                </li>
                            ))}
                        </ul>
                        <input
                            type="text"
                            value={newRoom}
                            onChange={(e) => setNewRoom(e.target.value)}
                            placeholder="Create a new room"
                            className="input-field"
                        />
                        <button onClick={handleCreateRoom} className="create-room-button">
                            Create Room
                        </button>
                    </div>
                    {currentRoom && (
                        <>
                            <h3>Room: {currentRoom}</h3>
                            <div className="messages-container">
                                {messages.map((msg, index) => (
                                    <div key={index} className="message">
                                        <strong>{msg.username}:</strong> {msg.message}
                                    </div>
                                ))}
                            </div>
                            <div className="input-container">
                                <input
                                    type="text"
                                    placeholder="Type a message"
                                    value={message}
                                    onChange={(e) => setMessage(e.target.value)}
                                    className="input-field"
                                />
                                <button onClick={handleSendMessage} className="send-button">
                                    Send
                                </button>
                            </div>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default App;
